	adresTypeID int NULL
)
GO

CREATE TABLE JoinPersoonsVerzekeringToPersoon ( 
	persoonID int NULL,
	persoonsVerzekeringID int NULL
)
GO

CREATE TABLE JoinPersoonVrijVeldTypeToPersoon ( 
