)
GO

CREATE TABLE JoinPersoonToVerzekeringType ( 
	verzekeringTypeID int NULL,
	persoonID int NULL
)
GO

CREATE TABLE Persoon ( 
	AdNummer int NULL,
	Naam char(10) NULL,
	VoorNaam char(10) NULL,
	GeboorteDatum smalldatetime NULL,
	Geslacht Geslachtslijst NULL,
	ChiroLeefTijd short DEFAULT 0 NULL,    --  Dit veld geeft weer of een lid 'op zijn leeftijd' zit voor de automatische berekening van de afdeling.  Standaard heeft het de waarde 0.  Als een lid een jaartje hoger zit dan zijn/haar geboortedatum doet vermoeden (bijv. omdat hij/zij een leerjaar oversloeg) , dan zal dit veld op +1 staan. 
	SterfDatum smalldatetime NULL,
	Guid UniqueIdentifier NULL,
	persoonID int NOT NULL
)
GO

CREATE TABLE OfficieleAfdeling ( 
	Naam char(10) NULL,
	officieleAfdelingID int NOT NULL
)
GO

CREATE TABLE LidVrijVeldType ( 
	WerkJaarVan int NULL,
	WerkJaarTot int NULL,
	lidVrijVeldTypeID int NOT NULL
)
GO

CREATE TABLE LidVrijVeld ( 
	Waarde char(10) NULL,
	lidVrijVeldID int NOT NULL
)
GO

CREATE TABLE JoinLidVrijVeldTypeToLid ( 
	lidID int NULL,
	lidVrijVeldTypeID int NULL
)
GO

CREATE TABLE Lid ( 
	LidgeldBetaald bit NULL,
	VolgendWerkjaar ToekomstPerspectief NULL,    --  Bij een nieuw werkjaar zijn er geen leden meer.  Maar aan de hand van wat er in 'VolgendWerkjaar' staat, kan het programma aangeven of er nog leden zijn die waarschijnlijk nog ingeschreven moeten worden. 
--  Als een lid van vorig werkjaar als toekomstperspectief 'KomtTerug' heeft, dan herinnert het programma er aan dat die persoon nog aangesloten moet worden.  Is het toekomstperspectief 'KomtNietTerug', dan zal het programma dat niet doen. 
	lidID int NOT NULL,
	groepsWerkjaarID int NULL,
	persoonID int NULL
)
GO

CREATE TABLE Leiding ( 
	DubbelPuntAbonnement bool NULL,
	leidingID int NOT NULL
)
GO

CREATE TABLE Land ( 
	Naam char(10) NULL,
	landID int NOT NULL,
	gemeenteID int NULL
)
GO

CREATE TABLE Kind ( 
	kindID int NOT NULL,
	afdelingsJaarID int NULL
)
GO

CREATE TABLE GroepsWerkjaar ( 
	WerkJaar int NULL,    --  Een werkjaar wordt geidentificeerd via het eerste van de twee kalender jaren.  Zo heeft bijv. voor 2007-2008 het veld 'Jaar' de waarde 2007, en voor 2008-2009 de waarde 2008. 
	groepsWerkjaarID int NOT NULL,
	groepID int NULL
)
GO

CREATE TABLE GelieerdePersoon ( 
	groepID int NULL,
	persoonID int NULL
)
GO

CREATE TABLE Groep ( 
	Naam char(10) NULL,
	Code char(10) NULL,    --  Voor een Chirogroep is de code het huidige stamnummer.  Als er ook gegevens van oud-leidingsploegen bewaard zullen worden op het nationaal secretariaat, zullen voor dit soort 'satellieten' ook codes bepaald moeten worden. 
	OprichtingsJaar int NULL,
	WebSite char(10) NULL,
	Logo image NULL,
	groepID int NOT NULL,
	woonplaatsID int NULL
)
GO

CREATE TABLE Gemeente ( 
	Naam char(10) NULL,
	gemeenteID int NOT NULL
)
GO

CREATE TABLE GeassocieerdMet ( 
	geassocieerdMetID int NOT NULL
)
GO

CREATE TABLE GavSchap ( 
	VervalDatum smalldatetime NULL,    --  GAV vervalt op 15 oktober van volgend werkjaar.  1 GAV kan zijn termijn verlengen, door het nieuwe werkjaar te initieren. 
	gavSchapID int NOT NULL
)
GO

CREATE TABLE JoinGroepToGav ( 
	gavID int NULL,
	groepID int NULL
)
GO

CREATE TABLE Gav ( 
	Login char(10) NULL,
	WachtWoord char(10) NULL,
	gavID int NOT NULL
)
GO

CREATE TABLE LidFunctie ( 
	lidID int NULL,
	functieID int NULL
)
GO

CREATE TABLE Functie ( 
	MaxAantal int NULL,    --  Als het aantal personen in een groep met deze functie een bovengrens heeft, dan is die bovengrens gegeven door 'maxAantal'.  Bijvoorbeeld: Hoogstens 1 contactpersoon per groep. 
	MinAantal int NULL,    --  Als het aantal personen in een groep met deze functie een ondergrens heeft, dan is die ondergrens gegeven door 'maxAantal'.  Bijvoorbeeld: Minstens 1 contactpersoon per groep. 
	MinimumLeefTijd int NULL,    --  Mogelijk is er een minimumleeftijd voor een bepaalde functie. 
	Naam char(10) NULL,    --  Naam van de functie 
	WerkJaarVan int NULL,    --  Vanaf wanneer deze functie beschikbaar gemaakt is voor de groep 
	WerkJaarTot int NULL,    --  Tot wanneer deze functie beschikbaar blijft voor de groep. Niet opgegeven = voor altijd. 
	functieID int NOT NULL
)
GO

CREATE TABLE EigenFunctie ( 
	eigenFunctieID int NOT NULL,
	groepID int NULL
)
GO

